$("li").on("click", function () {
    const player = players[$(this).data("player")];
    $(".modal-body").html(player.bio);
    $(".modal-goals").html(player.goals);
    $(".modal-caps").html(player.caps);
    $(".modal-nation").html(player.nationality);
    $(".modal-position").html(player.position);
    $(".modal-title").text(player.name);
    $('.modal').modal('show')
})